'use client';

import { revalidateTag } from "next/cache";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { signIn } from "next-auth/react";
import { Suspense, useRef, useState } from "react";



export default function page() {
    const router = useRouter();
    const email = useRef("");
    const pass = useRef("");
    const [isLogin, setIsLogin] = useState(true);
    const onSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (!email.current || !pass.current) {
            setIsLogin(false);
        }
        const res = await signIn("credentials", {
            email: email.current,
            password: pass.current,
            redirect: false,
        });
        if (!res?.error) {
            router.push("/");
            router.refresh();
        } else {
            setIsLogin(false);
            console.log("Error /signIn/page.tsx" + res.error);
            // alert("You entered wrong email or password!");
        }
    };

    return (
        <div className="h-[100vh] p-5 bg-pink-50">
            <div className="w-[666px] h-[702px] bg-gradient-to-b from-pink-200 rounded-[50px] shadow m-auto my-16">
                <div className="w-auto h-[61px] text-pink-500 text-5xl font-bold font-sans uppercase m-auto py-12">Sign In</div>
                <form className="text-zinc-900 text-3xl font-normal font-sans" onSubmit={onSubmit}>

                    <div className="relative my-6">
                        <input type="text"
                            className="pl-24 pr-4 py-2 border w-[462px] h-[67px] bg-white rounded-[50px]"
                            placeholder="Email"
                            name="email"
                            id="email"
                            onChange={(e) => (email.current = e.target.value)}
                        />
                        <div className="absolute inset-y-0 left-[120px] pl-3  
                    flex items-center  
                    pointer-events-none">
                            <img src="data:image/png;base64," alt="email" />
                        </div>
                    </div>

                    <div className="relative my-6">
                        <input type="password"
                            className="pl-24 pr-4 py-2 border w-[462px] h-[67px] bg-white rounded-[50px]"
                            placeholder="Password"
                            name="password"
                            id="password"
                            onChange={(e) => (pass.current = e.target.value)}
                        />
                        <div className="absolute inset-y-0 left-[120px] pl-3  
                    flex items-center  
                    pointer-events-none">
                            <img src="data:image/png;base64," alt="password" />
                        </div>
                    </div>

                    <div className="flex items-center justify-center gap-2 m-2 text-center text-white text-[40px] font-bold font-sans py-6">
                        <button
                            id="signin"
                            type="submit"
                            className="w-[372px] h-[68px] bg-gradient-to-r from-pink-200 to-pink-500 rounded-[20px] hover:bg-pink-300 active:bg-pink-400 focus:outline-none focus:ring-2 focus:ring-pink-600 focus:ring-opacity-50"
                        >
                            Sign In
                        </button>
                    </div>
                </form>
                <div className="w-[303px] h-[22px] text-center m-auto flex items-center justify-center">
                    <span className="text-black text-sm font-normal font-sans uppercase leading-[17.50px]">DONâ€™T have an account? </span>
                    <span className="text-white text-[15px] font-bold font-sans underline uppercase flex items-center">
                        <Link href={"/signUp"} className="text-pink-500">
                            REGISTER
                        </Link>
                        <span className="text-black text-sm font-semibold font-sans uppercase leading-[17.50px]"> HERE</span>
                    </span>
                </div>
            </div>
            {!isLogin && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded fixed top-28 right-5" role="alert">
                    <strong className="font-bold">Warning:</strong>
                    <span className="block sm:inline">Please fill in valid email and password.</span>
                </div>
            )}
        </div>
    );
}