"use client";

import userSignUp from "@/libs/userSignUp";
import { useRouter } from "next/navigation";
import { ChangeEvent, FormEvent, useRef, useState } from "react";
import axios from "axios";
import Link from "next/link";

const RegistrationForm = () => {
  const router = useRouter();
  const [isFill, setIsFill] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    telephone: "",
  });

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (
      !formData.name ||
      !formData.email ||
      !formData.password ||
      !formData.telephone
    ) {
      setIsFill(false);
      return;
    }

    try {
      const response = await userSignUp(formData);
      console.log("Registration successful:", response);
      router.push("/");
      router.refresh();
      alert("Registration Successful!");
    } catch (error) {
      console.error("Registration failed:", error);
    }
  };

  return (
    <div className="min-h-[100vh] p-5 bg-pink-50">
      <div className="w-[666px] h-[702px] bg-gradient-to-b from-pink-200 rounded-[50px] shadow m-auto my-16">
        <div className="w-auto h-[61px] text-pink-500 text-5xl font-bold font-sans uppercase m-auto py-12">
          Register form
        </div>
        <form
          className="text-zinc-900 text-3xl font-normal font-sans"
          onSubmit={handleSubmit}
        >
          <div className="relative my-6">
            <input
              type="text"
              className="pl-24 pr-4 py-2 border w-[462px] h-[67px] bg-white rounded-[50px]"
              placeholder="Username"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
            <div
              className="absolute inset-y-0 left-[120px] pl-3  
                flex items-center  
                pointer-events-none"
            >
              <img
                src="data:image/png;base64,"
                alt="username"
              />
            </div>
          </div>

          <div className="relative my-6">
            <input
              type="email"
              className="pl-24 pr-4 py-2 border w-[462px] h-[67px] bg-white rounded-[50px]"
              placeholder="Email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
            <div
              className="absolute inset-y-0 left-[120px] pl-3  
                flex items-center  
                pointer-events-none"
            >
              <img
                src="data:image/png;base64,"
                alt="email"
              />
            </div>
          </div>

          <div className="relative my-6">
            <input
              type="password"
              className="pl-24 pr-4 py-2 border w-[462px] h-[67px] bg-white rounded-[50px]"
              placeholder="Password"
              name="password"
              value={formData.password}
              onChange={handleChange}
            />
            <div
              className="absolute inset-y-0 left-[120px] pl-3  
                flex items-center  
                pointer-events-none"
            >
              <img
                src="data:image/png;base64,"
                alt="password"
              />
            </div>
          </div>

          <div className="relative my-6">
            <input
              type="string"
              className="pl-24 pr-4 py-2 border w-[462px] h-[67px] bg-white rounded-[50px]"
              placeholder="Telephone"
              name="telephone"
              value={formData.telephone}
              onChange={handleChange}
            />
            <div
              className="absolute inset-y-0 left-[120px] pl-3  
                flex items-center  
                pointer-events-none"
            >
              <img
                src="data:image/png;base64,"
                alt="telephone"
              />
            </div>
          </div>

          <div className="flex items-center justify-center gap-2 m-2 text-center text-white text-[40px] font-bold font-sans py-6">
            <button
              id="signin"
              type="submit"
              className="w-[372px] h-[68px] bg-gradient-to-r from-pink-200 to-pink-500 rounded-[20px] hover:bg-pink-300 active:bg-pink-400 focus:outline-none focus:ring-2 focus:ring-pink-600 focus:ring-opacity-50"
            >
              Sign Up
            </button>
          </div>
        </form>
      </div>
      {!isFill && (
        <div
          className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded fixed top-28 right-5"
          role="alert"
        >
          <strong className="font-bold">Warning:</strong>
          <span className="block sm:inline">
            Please fill in all information!
          </span>
        </div>
      )}
    </div>
  );
};

export default RegistrationForm;
